def request_nameen_place_durationmp3():
    # Запрос страницы
    page = requests.get('https://branham.pro/sermons/47-0412')

    # Создание объекта soup
    soup = BeautifulSoup(page.content, 'html.parser')

    # Поиск англ. названия
    name_en = soup.find(class_='text-center mb-3 font-weight-normal').text
    print(name_en)

    # Поиск "место проповеди"
    place = soup.find(class_='col my-auto').text
    print(place)

    # Поиск длительности
    duration_mp3 = soup.find_all(
        class_='whiteVal position-relative d-inline-block my-1')[1].text
    print(duration_mp3)


